contracts/AgentPay.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.27;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

contract AgentPay is Ownable {
    struct Invoice {
        uint256 id;
        address creator;
        address recipient;
        uint256 amount;
        address token; // address(0) for native MNT
        string metadataHash; // IPFS hash etc
        bool paid;
        uint256 dueDate;
    }

    struct AgentConfig {
        bool isActive;
        uint256 limitPerTx;
        address allowedRecipient;
    }

    uint256 public nextInvoiceId;
    mapping(uint256 => Invoice) public invoices;
    mapping(address => AgentConfig) public agentConfigs;

    event InvoiceCreated(uint256 indexed id, address indexed creator, address indexed recipient, uint256 amount);
    event InvoicePaid(uint256 indexed id, address indexed payer);
    event AgentConfigured(address indexed user, bool isActive);

    constructor() Ownable(msg.sender) {}

    function createInvoice(
        address _recipient,
        uint256 _amount,
        address _token,
        string memory _metadataHash,
        uint256 _dueDate
    ) external returns (uint256) {
        uint256 id = nextInvoiceId++;
        invoices[id] = Invoice({
            id: id,
            creator: msg.sender,
            recipient: _recipient,
            amount: _amount,
            token: _token,
            metadataHash: _metadataHash,
            paid: false,
            dueDate: _dueDate
        });

        emit InvoiceCreated(id, msg.sender, _recipient, _amount);
        return id;
    }

    function payInvoice(uint256 _id) external payable {
        Invoice storage invoice = invoices[_id];
        require(!invoice.paid, "Already paid");
        require(invoice.id == _id, "Invoice does not exist");

        if (invoice.token == address(0)) {
            require(msg.value == invoice.amount, "Incorrect amount");
            (bool sent, ) = invoice.recipient.call{value: msg.value}("");
            require(sent, "Failed to send MNT");
        } else {
            IERC20(invoice.token).transferFrom(msg.sender, invoice.recipient, invoice.amount);
        }

        invoice.paid = true;
        emit InvoicePaid(_id, msg.sender);
    }
}

